package org.dasdarki.example;

import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.context.annotation.Bean;

/**
 * This class was generated by YmirScript. Do not edit it manually.
 */
@EnableWebSecurity
public class CorsConfiguration {

    @Bean
    public void filterChain(HttpSecurity http) throws Exception {
        String allowedOrigin = System.getenv("ALLOWED_ORIGIN");
        http.cors().configurationSource(request -> new CorsConfiguration().applyPermitDefaultValues().setAllowedOrigins(Collections.singletonList(allowedOrigin)));
    }

}
